import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'courses-app';
}
<app-header>
  <button class="header-button">Log out</button>
  <button class="header-button">Log in</button>
</app-header>
<div class="app-root">
  <app-search></app-search>

  <app-info></app-info>

  <app-course-card></app-course-card>

  <app-login-form></app-login-form>

  <app-registration-form></app-registration-form>

  <app-course-form></app-course-form>

  <!-- <app-modal [message]="'Message'" [title]="'Title'" [okButtonText]="'Ok'" [cancelButtonText]="'Cancel'"></app-modal> -->
</div>
