import { Component } from '@angular/core';
import { mockedCoursesList } from './shared/mocks/mocks';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'courses-app';
  courses = mockedCoursesList; 

  onShowCourse(id: string) {
    console.log('Show course', id);
  }

  onEditCourse(id: string) {
    console.log('Edit course', id);
  }

  onDeleteCourse(id: string) {
    console.log('Delete course', id);
  }
}
<app-header>
  <button class="header-button">Log out</button>
  <button class="header-button">Log in</button>
</app-header>

<div class="app-root">
  <!--<app-search></app-search>-->

  <app-info
    title="Your Cart Is Empty"
    text="Add items to your cart to see them here."
  >
    <app-button buttonText="Add New Course"></app-button>
  </app-info>
  <app-course-info></app-course-info>
  
  <h2>Тест CoursesList</h2>

<pre>{{ courses | json }}</pre>

<app-courses-list
  [courses]="courses"
  [editable]="true"
  (showCourse)="onShowCourse($event)"
  (editCourse)="onEditCourse($event)"
  (deleteCourse)="onDeleteCourse($event)"
></app-courses-list>


  <!--<app-login-form></app-login-form>-->

  <!--<app-registration-form></app-registration-form>-->

  <!--<app-course-form></app-course-form>-->

  <!-- <app-modal [message]="'Message'" [title]="'Title'" [okButtonText]="'Ok'" [cancelButtonText]="'Cancel'"></app-modal> -->
</div>
